import React, { useEffect, useState } from "react";

import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Container from "react-bootstrap/Container";

import appStyles from "../../App.module.css";

import { useLocation } from "react-router";
import { axiosReq } from "../../api/axiosDefaults";
import Task from "./Task";

function  TasksPage({message, filter =""}) {

  const [tasks, setTasks] = useState({ results: [] });
  const [hasLoaded, setHasLoaded] = useState(false);
  const { pathname } = useLocation();

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const { tasks } = await axiosReq.get(`/tasks/?${filter}`);
        setTasks(tasks);
        setHasLoaded(true);
      } catch (err) {
        console.log(err);
      }
    };

    setHasLoaded(false);
    fetchTasks();
  }, [filter, pathname]); //This code runs when either of these 2 values change


  return (
    <Row className="h-100">
      <Col className="py-2 p-0 p-lg-2" lg={8}>
        <p>Popular profiles mobile</p>
        <p>List of posts here</p>
        {hasLoaded ? (
          <>
            {tasks.length ? (
              tasks.results.map((task) => (
                <Task key={task.id} {...task} setTasks={setTasks} />
              ))
            ) : (
              <Container className={appStyles.Content}>
              {/* <Asset src={NoResults} message={message} /> */}
              </Container>
            )}
          </>
        ) : (
          <Container className={appStyles.Content}>
            {/* <Asset spinner /> */}
          </Container>
        )}
      </Col>
      <Col md={4} className="d-none d-lg-block p-0 p-lg-2">
        <p>Popular profiles for desktop</p>
      </Col>
    </Row>
  );
}

export default TasksPage;
