import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Task from './Task';
import TaskView from './TaskView';
import { Button, Card } from "react-bootstrap";
import { useHistory } from 'react-router-dom';


function TasksPage() {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('');
  const [loading, setLoading] = useState(true);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editedTask, setEditedTask] = useState({
    id: '',
    task_name: '',
    description: '',
    owner: '',
    created_at: '',
    status: ''
  });

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get(`/tasks?filter=${filter}`);
        setTasks(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching tasks:', error);
        setLoading(false);
      }
    };

    fetchTasks();
  }, [filter]);

  const handleFilterChange = (event) => {
    setFilter(event.target.value);
  };

  const handleDeleteClick = async (taskId) => {
    try {
      await axios.delete(`/tasks/${taskId}`);
      alert("Task successfully deleted")
            // Refresh the page after deletion
            window.location.reload();
    } catch (error) {
      console.error('Error deleting task:', error);
      alert("You cannot delete another user's tasks")

    }
  };

  const handleEditClick = (task) => {
    setEditedTask(task);
    setShowEditModal(true);
  };

  return (
    <div>
      <h1>Tasks list</h1>
      <input
        type="text"
        placeholder="Search..."
        value={filter} 
        onChange={handleFilterChange}
      />
      {loading ? (
        <p>Loading...</p>
      ) : (
        <ul>
          {tasks.map((task) => (
            <>
              <Card>
                  <Card.Body>
                      <Card.Title className="text-center">{task.task_name}</Card.Title>
                      <Card.Text>{task.description}</Card.Text>
                      Owner: {task.owner}<br></br>
                      Date created: {task.created_at}<br></br>
                      Status: {task.status}<br></br>
                  </Card.Body>
                  <Button onClick={() => handleDeleteClick(task.id)}>Delete</Button>
                  <br></br>
                  <Button style={c} onClick={() => handleEditClick(task)}>Edit</Button>


              </Card>
            </>
          ))}
        </ul>
      )}
    </div>
  );
}

export default TasksPage;
